import{a as e,o,j as d}from"./index-e8a96955.js";const n={class:"markdown-body"},u=d(`<h2>Button</h2><p><code>Button</code> provides various types, styles, states and icons.</p><h3>Example</h3><ul><li><p>Button type</p><p>The default type is <code>button</code>, and you can set that to <code>submit</code> in form.</p><pre><code class="language-html">&lt;cube-button&gt;Button&lt;/cube-button&gt;
&lt;cube-button type=&quot;submit&quot;&gt;Submit Button&lt;/cube-button&gt;
</code></pre></li><li><p>Button state</p><p>Default is normal, can be set to active or disabled state.</p><pre><code class="language-html">&lt;cube-button :active=&quot;true&quot;&gt;Active Button&lt;/cube-button&gt;
&lt;cube-button :disabled=&quot;true&quot;&gt;Disabled Button&lt;/cube-button&gt;
</code></pre></li><li><p>Icon</p><p>You can set the class of <code>icon</code>.</p><pre><code class="language-html">&lt;cube-button icon=&quot;cubeic-right&quot;&gt;Button With Icon&lt;/cube-button&gt;
</code></pre></li><li><p>Style</p><p>You can set attributes like <code>light</code>, <code>inline</code>, <code>outline</code>, and <code>primary</code> to change the button’s style.</p><pre><code class="language-html">&lt;cube-button :light=&quot;true&quot;&gt;Light Button&lt;/cube-button&gt;
&lt;cube-button :inline=&quot;true&quot;&gt;Inline Button&lt;/cube-button&gt;
&lt;cube-button :outline=&quot;true&quot;&gt;Outline Button&lt;/cube-button&gt;
&lt;cube-button :primary=&quot;true&quot;&gt;Primary Button&lt;/cube-button&gt;
</code></pre></li></ul><h3>Props configuration</h3><table><thead><tr><th>Attribute</th><th>Description</th><th>Type</th><th>Accepted Values</th><th>Default</th></tr></thead><tbody><tr><td>type</td><td>Button type</td><td>String</td><td>button/submit</td><td>button</td></tr><tr><td>active</td><td>active state</td><td>Boolean</td><td>true/false</td><td>false</td></tr><tr><td>disabled</td><td>disabled state</td><td>Boolean</td><td>true/false</td><td>false</td></tr><tr><td>icon</td><td>Icon</td><td>String</td><td>the class of icon</td><td>-</td></tr><tr><td>light</td><td>light style</td><td>Boolean</td><td>true/false</td><td>false</td></tr><tr><td>inline</td><td>whether inline</td><td>Boolean</td><td>true/false</td><td>false</td></tr><tr><td>outline</td><td>outline style</td><td>Boolean</td><td>true/false</td><td>false</td></tr><tr><td>primary</td><td>primary style</td><td>Boolean</td><td>true/false</td><td>false</td></tr></tbody></table><h3>Events</h3><table><thead><tr><th>Event Name</th><th>Description</th><th>Parameters</th></tr></thead><tbody><tr><td>click</td><td>triggers when the button is clicked. If in disabled state, then it won’t trigger</td><td>e - event target</td></tr></tbody></table>`,8),l=[u],b={__name:"button",setup(a,{expose:t}){return t({frontmatter:{}}),(r,i)=>(o(),e("div",n,l))}};export{b as default};
